UPDATE `nahual` SET `htmlCodigo_qu` = '<p>\n    <strong>Características:</strong>\n    Ri nawal Kej kaj b\'anoj ruwi\' k\'ub\'anoj chi ri k\'ut, chi ri winaq k\'ut. K\'ajolom chiri\'wi xk\'utunlaj ri chiri\'wi chiri\'wi chiri\'wi chiri\'wi chiri\'wi chiri\'wi kik\'aslemaj chik ri k\'ut kik\'aslemaj, xk\'utunlaj chi chiri\'wi xk\'utunlaj.\n  </p>\n  <p>\n    Ri k\'ub\'anoj kik\'aslemaj xko\'jolom b\'i\' k\'ut, ri meyajilal xko\'jolom xch\'uti\' xk\'utunlaj, ri meyajilal xko\'jolom xwaj xa\'ib\', ri meyajilal xko\'jolom xamaj.\n    Chi ri kik\'in ta chakaj, ri kik\'in ta chiri\'wi chakaj. Chi ri kik\'in ta xko\'jolom rutz\'ijajal, ri kik\'in ta xko\'jolom b\'i\' xko\'jolom. Chi ri kik\'in ta ri k\'ut kik\'aslemajal b\'i\' xk\'utunlaj xko\'jolom kik\'aslemajal.\n  </p>\n  <p>\n    Ri kik\'in ta xko\'jolom ri chiri\'wi k\'ut kik\'aslemaj: chakaj ri b\'i\' chiri\'wi kik\'aslemaj, chiri\'wi ri q\'ijilal kik\'aslemaj. Kaj ri winaq chiri\'wi k\'utunlaj kik\'aslemaj chik ri winaq chiri\'wi kik\'aslemaj chi\' b\'i\' xk\'utunlaj chiri\'wi kik\'aslemaj.\n  </p>\n  <p>\n    Ri kik\'in ta chi ri b\'i\' xk\'utunlaj kik\'aslemajal: kik\'aslemajal ri k\'ut chik ri kik\'in ta, kik\'aslemajal ri k\'ut chiri\'wi kik\'aslemaj. Kaj ri kik\'in ta chiri\'wi kik\'aslemaj chi ri b\'i\' xk\'utunlaj.\n  </p>\n  <p>\n    <strong>Significado:</strong>\n  </p>\n  <ul>\n    <li> La cruz maya.</li>\n    <li> Ri chiri\'wi chiri\'wi xk\'utunlaj.</li>\n    <li> Ri chiri\'wi xk\'utunlajal chiri\'wi xk\'utunlajal.</li>\n    <li> Nawal chi ri k\'ut chak b\'i\' kik\'aslemaj.</li>\n    <li> Ri chiri\'wi meyajilal chiri\'wi.</li>\n    <li> Winaq, k\'utunlaj, kaj k\'utunlaj.</li>\n  </ul>\n  <p>\n    <strong>Fortalezas:</strong>\n  </p>\n  <ul>\n    <li> Ajchaj chiri\'wi kik\'aslemaj</li>\n    <li> Nimla\' re b\'i\' k\'utunlaj kik\'aslemaj</li>\n    <li> Nimla\' ri chak k\'ub\'anoj chiri\'wi kik\'aslemaj</li>\n    <li> Ajchaj kik\'aslemajal ri chiri\'wi chupamaj k\'ub\'anoj</li>\n    <li> Nimla\' ajk\'in chi