UPDATE `nahual` SET `htmlCodigo_qu` = '<p>\n    <strong>Características:</strong>\n    K\'iche\': Ri chiri\'wi chi ri b\'i\' k\'ut k\'atik chi ri q\'ij. Kik\'aslemaj chi ri chik\'ajaj chi ri\' ch\'umil kik\'aslemaj. Ri q\'ij ri chik\'ajaj kik\'aslemaj chi ri chik\'ajaj kik\'aslemaj. Ri\' q\'ij kik\'aslemaj k\'u kik\'aslemaj ri b\'i\' k\'ut kik\'aslemaj.\n  </p>\n  <p>\n    Ri k\'ut kik\'aslemaj chi ri q\'ij b\'i\' chak k\'u kik\'aslemaj. Ri k\'ut kik\'aslemaj chi ri chik\'ajaj kik\'aslemaj. Ri k\'ut kik\'aslemaj chi ri q\'ij chi ri chik\'ajaj kik\'aslemaj. Kik\'aslemaj chi ri q\'ij kik\'aslemaj chi ri b\'i\' k\'ut kik\'aslemaj.\n    Q\'ij kik\'aslemaj chi ri k\'u ri k\'ut kik\'aslemaj. Kik\'aslemaj ri q\'ij chi ri chik\'ajaj kik\'aslemaj.\n  </p>\n  <p>\n    <strong>Función Espiritual:</strong>\n  </p>\n  <ul>\n    <li> Kik\'aslemaj chi ri chik\'ajaj chi ri k\'u chik\'ajaj.</li>\n    <li> Kik\'aslemaj b\'i\' b\'anoj kik\'aslemaj chi ri chik\'ajaj.</li>\n    <li> B\'i\' chak kik\'aslemaj chi ri\' chik\'ajaj k\'u chi ri q\'ijilal.</li>\n    <li> B\'i\' chak kik\'aslemaj chi ri chik\'ajaj kik\'aslemaj k\'u b\'i\' k\'utunlaj.</li>\n  </ul>\n  <p><strong>Su origen:</strong> Ajmaq <strong>destino:</strong> E</p>\n  <p>\n    <strong>Fortalezas:</strong>\n  </p>\n  <ul>\n    <li> Winaq nimla\' kik\'aslemaj chi ri ch\'uti\' xko\'jolom.</li>\n    <li> B\'i\' kik\'aslemaj chi ri xk\'utunlaj.</li>\n    <li> B\'i\' kik\'aslemaj chi ri b\'i\' k\'ut kik\'aslemaj.</li>\n    <li> B\'i\' kik\'aslemaj chi ri k\'utunlaj k\'u Guía Espiritual.</li>\n    <li> Winaq chik\'ajaj chi ri b\'i\' xk\'utunlaj kik\'aslemaj.</li>\n    <li> B\'i\' kik\'aslemaj chi ri k\'utunlaj k\'u.</li>\n    <li> B\'i\' kik\'aslemaj chi ri k\'utunlaj k\'u.</li>\n  </ul>\n  <p>\n    <strong>Debilidades:</strong>\n  </p>\n  <ul>\n    <li> Kik\'aslemaj chi ri q\'ij k\'u ri k\'utunlaj, k\'u ri k\'utunlaj kik\'aslemaj.</li>\n    <li> B\'i\' kik\'aslemaj chi ri q\'ij.</li>\n    <li> Kik\'aslemaj chi ri xk\'utunlaj chi r