UPDATE `nahual` SET `htmlCodigo_qu` = '<p>\n    <strong>Características:</strong>\n    Ri kik\'in ta jun winaq chik\'ama\' b\'i\' xk\'utunlaj kik\'aslemaj ri k\'iche\', chi\' chiri\'wi chiri\'wi chiri\'wi chiri\'wi kik\'aslemaj chi ri\' k\'otz\'ijaj kik\'aslemaj b\'i\' jun ri b\'i\' xk\'utunlaj chiri\'wi k\'otz\'ijaj kik\'aslemaj.\n  </p>\n  <p>\n    Kik\'in ta jun nawal chi ri k\'otz\'ijaj kik\'aslemaj. Chiri\'wi kik\'aslemajal ri winaq q\'ijilal chiri\'wi kik\'aslemaj. Chiri\'wi k\'utuj chi ri k\'otz\'ijaj kik\'aslemaj chi\' ri b\'i\' xk\'utunlaj chiri\'wi kik\'aslemaj.\n  </p>\n  <p>\n    Ri kik\'aslemaj k\'ub\'anoj ri meyajilal chi ri b\'i\' xk\'utunlaj kik\'aslemaj b\'i\' meso América: xik\'ojolom, tzutzuk, ch\'uti\' chiri\'wi. Ri meyajilal b\'i\' chiri\'wi kik\'aslemaj chi ri winaq k\'iche\'. Ri meyajilal k\'ub\'anoj ri\' xk\'utunlaj. Chi kik\'in ta chi ri b\'i\' xk\'utunlaj kik\'aslemaj chiri\'wi kik\'aslemaj.\n  </p>\n  <p>\n    <strong>Significado:</strong>\n  </p>\n  <ul>\n    <li> Nawal chi ri b\'i\' semilla.</li>\n    <li> Chiri\'wi q\'ijilal chi jun chiri\'wi k\'utuk k\'utuj winaq.</li>\n    <li> Winaq chi ri winaq chak b\'i\' q\'ijilal k\'utuk chiri\'wi kik\'aslemaj.</li>\n  </ul>\n  <p><strong>Día de:</strong> La Fecundación y de La Germinación.</p>\n  <p>\n    <strong>Fortalezas:</strong>\n  </p>\n  <ul>\n    <li> Ajchaj chi ri chakaj chi ri chiri\'wi chik\'ama\' chupamaj</li>\n    <li> Nimla\' re b\'i\' k\'utunlaj chik\'ama\' ch\'uti\' xk\'utunlaj</li>\n    <li> Nimla\' ri chak k\'ub\'anoj chiri\'wi kik\'aslemajal ri chiri\'wi</li>\n    <li> Ajchaj kaj kik\'aslemajal ri k\'utuk k\'utuj kik\'aslemaj ri\' xk\'utunlaj</li>\n    <li> Nimla\' ajk\'in</li>\n    <li> Nimla\' kik\'aslemaj ri q\'ijilal, chupamaj, q\'atb\'anoj, chweq\', k\'utunlaj</li>\n    <li> Ajchaj ajk\'in chik ri\' chi ri winaq k\'utunlaj</li>\n    <li> Q\'atb\'anoj chik ri b\'i\' k\'utunlaj</li>\n    <li> Chiri\'wi b\'anoj kik\'aslemajal ri chiri\'wi chupamaj chi kik\'aslemajal ri xk\'u