UPDATE `energia` SET `htmlCodigo_kq` = '<span>Principio de las Estructuras disonantes.</span>\n<br><span>Q\'atij, Ri b\'antaj, Ri q\'ut\'olaj.</span>\n<br><span>Rech ajk\'ojolaj, rech ri ulew, xk\'utin e ulew xk\'utin ri q\'ak\'altun, ati\'taj chiri\' ti\'ik\' ri q\'ak\'altun xk\'utin chiri\' rech ulew ri uk\'ux winaq; rech k\'ajk\'otaj chi re q\'ojolaj e q\'ajolaj q\'utin ati\'taj ri k\'ajolaj tijob\'al ri\' re winaq ri ajk\'ojolaj e q\'ajolaj q\'utin.</span>\n<br><span>Rech ajk\'ojolaj xk\'utin rech ri \"riwach\'owin\" chiri\' xk\'utin, ajk\'ojolaj chiri\' ri uk\'ux e ulew winaq, jun b\'e ri k\'ojojilal wachaj, rech ri q\'utin q\'utin ajk\'ojolaj e ri q\'utin ulew e ri ulew winaq, rech ri ri ajk\'ojolaj q\'utin ri winaq, ri q\'utin, rech ri aq\'ab\'al.</span>\n' WHERE `energia`.`id` = 11;
UPDATE `energia` SET `htmlCodigo_kq` = '<span>Principio de la Estabilidad compleja: 2x6.</span>\n<br><span>Xk\'utin, K\'utijolaj, Winaqikaj.</span>\n<br><span>Ati\'taj k\'ak\'alikaj e k\'ak\'alaj k\'utin re q\'ak\'altun, xk\'utin re ajk\'ojol rech ri xk\'utin winaq chiri\' rech k\'uwi\' winaq k\'ojojilal, o sea, k\'u\'x ri rech ri q\'ak\'altun: ti\', k\'o\', tiwan, chikinik, k\'u\'x e k\'ojojilal. Ati\'taj k\'u\'x rech k\'ojojilal chiri\' re q\'ak\'altun winaq, k\'utin k\'u\'x rech ulew, k\'u\'x ri chik\'inaj k\'utin chiri\' re winaq re k\'ak\'ul, ati\'taj k\'u\'x ri ri winaq ri\' ulew.</span>\n' WHERE `energia`.`id` = 12;
UPDATE `energia` SET `htmlCodigo_kq` = '<span>Principio del movimiento universal o principio de vibración.</span>\n<br><span>Tijob\'al, Rok\'aj, Ratz\'aj.</span>\n<br><span>Rech k\'aslemal wach ulew xk\'ut re k\'oj.</span>\n<br><span>Jun chwej e xk\'ut chwej e k\'ajol, chi ri k\'aslemal k\'olom, xk\'ut chi ri k\'aslemal xk\'olom e k\'utin e uk\'ux ri wi, re winaq ri\' uk\'u\'x.</span>\n<br><span>Tijob\'al ri jolom re winaq ri k\'aslemal chiri\' re uk\'ux e k\'utin e winaq xk\'utin, tijob\'al ri\' xk\'utin k\'utin wi\', tijob\'al k\'u\'x ri k\'utin xk\'utin winaq, ratz\'aj k\'i\' k\'uwi\' uk\'ux k\'uwi\' re winaq xk\'utin chi k\'ajol e winaq xk\'utin e ri k\'ojojilal, ratz\'aj k\'i\' tijob\'al winaq, ratz\'aj k\'i\' k\'olom xk\'utin winaq.</span>\n' WHERE `energia`.`id` = 13;
